import { Chain } from 'viem/chains';

type GetTokenUrlParameters = {
    /** Address of smart contract on the given chain */
    address: string;
    /** Optional ID of token */
    tokenId?: string;
    /** Chain namespace (default: 'eip155') */
    chainNamespace?: string;
} & ({
    /** ID of the chain */
    chainId: string | number;
    chain?: never;
} | {
    chainId?: never;
    /** Object with chain ID as a property (e.g. viem's `Chain` object) */
    chain: Pick<Chain, "id">;
});
/** Constructs a [CAIP-10](https://github.com/ChainAgnostic/CAIPs/blob/main/CAIPs/caip-10.md) compliant URL given the address, chain, and token ID (optional) as inputs */
declare function getTokenUrl({ address, tokenId, chainNamespace, chain: chainArg, chainId: chainIdArg, }: GetTokenUrlParameters): string;

export { getTokenUrl };
